<?php/** * @version		$Id: $ * @author		Codextension * @package		Joomla! * @subpackage	Module * @copyright	Copyright (C) 2008 - 2012 by Codextension. All rights reserved. * @license		GNU/GPL, see LICENSE */// no direct accessdefined( '_JEXEC' ) or die( 'Restricted access' );  defined('JPATH_BASE') or die;jimport('joomla.form.formfield');class JFormFieldAsset extends JFormField {    protected $type = 'Asset';    protected function getInput() {				//JHTML::_('behavior.mootools');		$checkJqueryLoaded = false;		$document	= &JFactory::getDocument();		$header = $document->getHeadData();		foreach($header['scripts'] as $scriptName => $scriptData)		{			if(substr_count($scriptName,'jquery')){				$checkJqueryLoaded = true;			}		}		//Add js		if(!$checkJqueryLoaded) 		$document->addScript(JURI::root().$this->element['path'].'js/jquery.min.js');		//$document->addScript(JURI::root().$this->element['path'].'js/chosen.jquery.min.js');				$document->addScript(JURI::root().$this->element['path'].'js/colorpicker/colorpicker.js');        $document->addScript(JURI::root().$this->element['path'].'js/jl.js');		$document->addScriptDeclaration('			var baseurl = "'.str_replace('administrator/', '',JURI::base()).'";			var moduleid = "'.JRequest::getInt("id").'";		');				//Add css         		$document->addStyleSheet(JURI::root().$this->element['path'].'css/jl.css');        $document->addStyleSheet(JURI::root().$this->element['path'].'js/colorpicker/colorpicker.css');                $document->addStyleSheet(JURI::root().$this->element['path'].'css/chosen.css');                                return null;    }}?>